package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// int Data Type
		int i =10;
		i = 20;
		int j =30;
		System.out.println(i+j); //50
		
		// double data type
		double d1= 12.33;
		double d2 = 100;   //100.00
		System.out.println(d2);
		
		// char data type - Only single digit value, should be written in single quotes
		char c1= 'a';
		char c2= 'A';
		char c3= '1';
		System.out.println(c2);
		
		//boolean  - Only true or false --- true & false boolean values as well as keywords in java.
		boolean b1=true;
		boolean b2=false;
		System.out.println(b2);
		
		//String - is a class but yes can be used as a data type. should be written within double quotes
		String s1 = "Hello World";
		String s2= "1000";
		String s3="12.33";
		System.out.println(s1);

	}

}